function isValid(s) {
    const stack = [];
  
    for (const c of s) {
      if (c === "(" || c === "{" || c === "[") {
        stack.push(c);
      }
  
      else if (c === ")" && stack.pop() !== "(") {
        return false;
      } else if (c === "}" && stack.pop() !== "{") {
        return false;
      } else if (c === "]" && stack.pop() !== "[") {
        return false;
      }
    }
  
    return stack.length === 0;
  }

  console.log(isValid("()"));
  console.log(isValid("()[]{}"));
  